    private void jButtonSomarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSomarActionPerformed
        switch (operacao) {
            case '-' -> {
               operacao = '+';
               if ("".equals(resultado)) {
                    num1 = Float.parseFloat(text1);
                    num2 = Float.parseFloat(text2);
                    resultado = Float.toString(num1 - num2);
                    jTextField.setText(resultado + " " + operacao + " ");
                } else {
                    num1 = Float.parseFloat(resultado);
                    num2 = Float.parseFloat(text3);
                    resultado = Float.toString(num1 - num2);
                    text3 = "";
                    jTextField.setText(resultado + " " + operacao + " ");
                }
            }          
            case '*' -> {
               operacao = '+';
               if ("".equals(resultado)) {
                    num1 = Float.parseFloat(text1);
                    num2 = Float.parseFloat(text2);
                    resultado = Float.toString(num1 * num2);
                    jTextField.setText(resultado + " " + operacao + " ");
                } else {
                    num1 = Float.parseFloat(resultado);
                    num2 = Float.parseFloat(text3);
                    resultado = Float.toString(num1 * num2);
                    text3 = "";
                    jTextField.setText(resultado + " " + operacao + " ");
                }
            }
            case '/' -> {
                operacao = '+';
               if ("".equals(resultado)) {
                    num1 = Float.parseFloat(text1);
                    num2 = Float.parseFloat(text2);
                    resultado = Float.toString(num1 / num2);
                    jTextField.setText(resultado + " " + operacao + " ");
                } else {
                    num1 = Float.parseFloat(resultado);
                    num2 = Float.parseFloat(text3);
                    resultado = Float.toString(num1 / num2);
                    text3 = "";
                    jTextField.setText(resultado + " " + operacao + " ");
                }
            }
            default -> {
                operacao = '+';
                if (contSoma == 0 && "".equals(text2)) {
                    jTextField.setText(text1 + " + ");      
                } else if (contSoma == 1 && "".equals(resultado)) {
                    num1 = Float.parseFloat(text1);
                    num2 = Float.parseFloat(text2);
                    resultado = Float.toString(num1 + num2);
                    jTextField.setText(resultado + " " + operacao + " ");
                } else {
                    num1 = Float.parseFloat(resultado);
                    num2 = Float.parseFloat(text3);
                    text3 = "";
                    resultado = Float.toString(num1 + num2);
                    jTextField.setText(resultado + " " + operacao + " ");
                } 
            }
        }
        contSoma++;
    }//GEN-LAST:event_jButtonSomarActionPerformed

